block.correctcode:
#include <iostream>
#include <string>

using namespace std;

//initialize connect 4 board to contain all
//empty cells
void initializeBoard();

//display connents of board
void displayBoard();

//Global variable for representing game board
char board[7][6];

//Add one piece to board
bool addPiece(int col, bool isPlayer1);

//Checks if game is over
bool isGameOver(bool isPlayer1);

//Declare the winner
void declareWinner(bool isPlayer1);

const int COLUMNS = 7;
const int ROWS = 6;

int main()
{
	cout << "Welcome to UOIT Connect 4!" << endl;
	//Setup and display initial game board
	initializeBoard();
	displayBoard();

	//Create a game play loop
	bool PlayerOne = true;
	while (!isGameOver(!PlayerOne))
	{
		bool validCol = false;
		int colToPlay = -1;
		//ask current player for column until valid and available
		//column is selected
		while (!validCol)
		{
			cout << "Player ";
			if (PlayerOne)
			{
				cout << "1";
			}
			else
			{
				cout << "2";
			}
			cout << ": Select a column (0-6) to play: ";
			cin >> colToPlay;
			validCol = addPiece(colToPlay, PlayerOne);
			if (!validCol)
			{
				cout << "Bad column value! Try again." << endl;
			}
		}

		displayBoard();
		PlayerOne = !PlayerOne;
	}
	return 0;
}

block.smellcode:
void initializeBoard()
{
	for (int col = 0; col < 7; col++)
	{
		for (int row = 0; row < ROWS; row++)
		{
			board[col][row] = '-';
		}
	}
}

void displayBoard()
{
	cout << endl << "Connect 4 Board:" << endl;
	for (int row = 0; row < 6; row++)
	{
		for (int col = 0; col < 7; col++)
		{
			cout << board[col][row] << " ";
		}
		cout << endl;
	}
	cout << "0 1 2 3 4 5 6" << endl;
}

block.correctcode:
void initializeBoard()
{
	for (int col = 0; col < COLUMNS; col++)
	{
		for (int row = 0; row < ROWS; row++)
		{
			board[col][row] = '-';
		}
	}
}

void displayBoard()
{
	cout << endl << "Connect 4 Board:" << endl;
	for (int row = 0; row < ROWS; row++)
	{
		for (int col = 0; col < COLUMNS; col++)
		{
			cout << board[col][row] << " ";
		}
		cout << endl;
	}
	cout << "0 1 2 3 4 5 6" << endl;
}

block.smellcode:
bool addPiece(int col, bool isPlayer1)
{
	//check if col is 0-6
	if ((col < 0) || (col > 6))
	{
		return false;
	}
	//check if col is full
	else if (board[col][0] != '-')
	{
		return false;
	}
	else
	{
		for (int row = 5; row >= 0; row--)
		{
			if (board[col][row] == '-')
			{
				if (isPlayer1)
				{
					board[col][row] = 'X';
				}
				else
				{
					board[col][row] = 'O';
				}
				break;
			}
		}
		return true;
	}
}

bool isGameOver(bool isPlayer1)
{
	char currPiece;
	if (isPlayer1)
	{
		currPiece = 'X';
	}
	else
	{
		currPiece = 'O';
	}

block.correctcode:
char getCurrentPiece(bool isPlayer1)
{
	char currentPiece;
	if (isPlayer1)
	{
		currentPiece = 'X';
	}
	else
	{
		currentPiece = 'O';
	}
	return currentPiece;
}

bool addPiece(int col, bool isPlayer1)
{
	//check if col is 0-6
	if ((col < 0) || (col > 6))
	{
		return false;
	}
	//check if col is full
	else if (board[col][0] != '-')
	{
		return false;
	}
	else
	{
		for (int row = 5; row >= 0; row--)
		{
			if (board[col][row] == '-')
			{
				board[col][row] = getCurrentPiece(isPlayer1);
				break;
			}
		}
		return true;
	}
}

bool isGameOver(bool isPlayer1)
{
	char currPiece = getCurrentPiece(isPlayer1);

	//Check if current player has won
	//4 pieces horizontally -- naive solution

	//Loop through all of the rows
	for (int r = 0; r < ROWS; r++)
	{
		int counter = 0;
		for (int i = 0; i < COLUMNS; i++)
		{
			if (board[i][r] == currPiece)
			{
				counter++;
			}
			else
			{
				counter = 0;
			}
			if (counter == 4)
			{
				declareWinner(isPlayer1);
				return true;
			}
		}
	}
	//4 pieces vertically
	for (int c = 0; c < COLUMNS; c++)
	{
		int counter = 0;
		for (int i = 0; i < ROWS; i++)
		{
			if (board[c][i] == currPiece)
			{
				counter++;
			}
			else
			{
				counter = 0;
			}
			if (counter == 4)
			{
				declareWinner(isPlayer1);
				return true;
			}
		}
	}
	//4 pieces diagonally (both ways!)


	//Check if out of moves
	bool movesLeft = false;
	for (int col = 0; col < COLUMNS; col++)
	{
		if (board[col][0] == '-')
		{
			movesLeft = true;
		}
	}
	if (!movesLeft)
	{
		cout << "Game Over :(. Out of moves." << endl;
		return true;
	}
	//Otherwise
	return false;
}

void declareWinner(bool isPlayer1)
{
	if (isPlayer1)
	{
		cout << "Congratulations Player 1! "
			<< "You Win!" << endl;
	}
	else
	{
		cout << "Congratulations Player 2! "
			<< "You Win!" << endl;
	}
}
