stuff.code:
float getCircleCircumference (float radius)
{
    return 3.14 * radius * 2;
}

float getCircleArea(float radius)
{
    return 3.14 * pow(radius, 2);
}

float getSphereVolume(float radius)
{
    return (4 * 3.14 * pow(radius, 3))/3;
}

float getSphereSurfaceArea(float radius)
{
    return 4 * 3.14 * pow(radius, 2);
}

stuff.correctcode:
float getCircleCircumference (float radius)
{
    return pi * radius * 2;
}

float getCircleArea(float radius)
{
    return pi * pow(radius, 2);
}

float getSphereVolume(float radius)
{
    return (4 * pi * pow(radius, 3))/3;
}

float getSphereSurfaceArea(float radius)
{
    return 4 * pi * pow(radius, 2);
}
