float GetAverageCost()
{
	float totalCost = 0;
	for(int i = 0; i < items[i].size(); i ++)
	{
		totalCost += items[i].cost;
	}

	return totalCost / items[i].size();
}
void PrintCustomerDetails()
{
	std::cout << customerName << std::endl;
	std::cout << "Average:" << GetAverageCost << std::endl;
}
